    private void signIn(){
        try {
            
            String query = "select usuario, password from usuarios where usuario = '" + txtUsuario.getText() 
                           + "' and password = '" + txtClave.getText() +"'";
            
            st = con.createStatement();
            rs = st.executeQuery(query);
            rs = st.getResultSet();
            
            // Open DashBoard after logged in
            
            if (rs.next()) {
                Inventario dash = new Inventario();
                dash.setVisible(true);
                this.setVisible(false);
            }
            
            // Checking for empty fields or putting the wrong password
            
            else if (txtUsuario.getText().isEmpty() || txtClave.getText().isEmpty()){
                JOptionPane.showMessageDialog(null, 
                        "Debe ingresar su usuario y contrase침a\n*Si no est치 registrado debe registrarse"
                );
            }
            
            else {
                JOptionPane.showMessageDialog(null, "Usuario o contrase침a incorrecta!");
            }
        }
        catch (SQLException e){ 
            JOptionPane.showMessageDialog(null, e);
        }
    
    }
    
    
